    public static void sort(int[] nums) {
        MaxHeap heap = new MaxHeap(nums);
        for (int i = 0; i < nums.length; i++) {
            System.out.print(heap.poll() + ",");
        }
    }

    static class MaxHeap {
        private int[] nums;
        private int size;
        private int capacity;

        public MaxHeap(int n) {
            // 下标0不存放元素
            nums = new int[n + 1];
            size = 0;
            capacity = n;
        }

        public MaxHeap(int[] arr) {
            nums = new int[arr.length + 1];
            size = arr.length;
            capacity = arr.length;
            System.arraycopy(arr, 0, nums, 1, arr.length);
            heapify();
        }

        private void heapify() {
            // 对每个元素执行上浮操作
            for (int index = 1; index <= size; index++) {
                int i = index;
                while (i > 1 && nums[i] > nums[i / 2]) {
                    swap(i, i / 2);
                    i /= 2;
                }
            }
        }

        public void add(int val) {
            if (size == capacity) {
                throw new IllegalStateException("Heap is full.");
            }
            // 将val插入到最后一个位置，然后上浮
            nums[++size] = val;
            int i = size;
            while (i > 1 && nums[i] > nums[i / 2]) {
                swap(i, i / 2);
                i /= 2;
            }
        }

        public int poll() {
            int res = peek();
            // 将最后一个元素放到堆头，然后下沉
            nums[1] = nums[size--];
            int i = 1;
            while (i * 2 <= size) {
                int left = i * 2;
                int right = i * 2 + 1;
                if (nums[i] < nums[left] || nums[i] < nums[right]) {
                    if (nums[left] > nums[right]) {
                        swap(i, left);
                        i = left;
                    } else {
                        swap(i, right);
                        i = right;
                    }
                } else {
                    break;
                }
            }
            return res;
        }

        public int peek() {
            if (size == 0) {
                throw new IllegalStateException("Heap is empty");
            }
            return nums[1];
        }

        private void swap(int i, int j) {
            int temp = nums[i];
            nums[i] = nums[j];
            nums[j] = temp;
        }
    }
